function isMintable()
    public
    view
    returns (bool)
{
    bool mintable = false;
    if (now - lastMintEvent > mintPeriodDuration && now <= schedules[6].endPeriod) // Ensure time is not after end of Year 7
    {
        mintable = true;
    }
    return mintable;
}

