function importIssuerData(address[] accounts, uint[] sUSDAmounts)
    external
    onlyOwner
    onlyDuringSetup
{
    require(accounts.length == sUSDAmounts.length, "Length mismatch");

    for (uint8 i = 0; i < accounts.length; i++) {
        _addToDebtRegister(accounts[i], sUSDAmounts[i]);
    }
}

